import React, {useState} from "react";
import PropTypes, {object} from "prop-types";
import { useParams, useSearchParams } from 'react-router-dom';
import Table from "react-bootstrap/Table";
import axios from "axios";
import Users from "./Users";

import "./Chat.css";


function Chat(props) {
    const params = useParams();
    const [searchParams, setSearchParams] = useSearchParams();
    console.log(searchParams);
    const [message, setMessage] = useState('');
    const [done_message, setDonemessage] = useState('');
    function handleClick() {
        console.log('Sending...');
        axios.post(`http://localhost:8000/api/messages/`, {
            'sender': "admin",
            'receiver': "test",
            'message': message,
        },
        {
            headers: {
                "Content-Type": 'application/json'
            }
        }
    )
    .then(res => console.log(res))
    .catch(error => console.err(error))

    }
//    <Users sender={props.sender} receiver={props.receiver}/>
    return (
        <React.Fragment>
            <div id="users">
                <Users sender={props.sender} receiver={props.receiver}/>
            </div>
            <form>
                <div id="board">

                </div>
                <div id="message_send">
                    <input id="message" type="text" name="message" onChange={(event) => setMessage(event.target.value)}/>
                    <input id="send" type="submit" value="Отправить" onClick={() => handleClick()} />
                </div>
            </form>
        </React.Fragment>
    )
}


export default Chat;
