import React, {useState} from "react";
import PropTypes, {object} from "prop-types";
import { useParams } from 'react-router-dom';
import Table from "react-bootstrap/Table";
import axios from "axios";
import UserData from "./UserData.js"

import "./Category.css";


function Users(props) {
    console.log(props);
    let {par} = useParams();
    console.log(par);
    const [sender, setSender] = useState('');
    const [receiver, setReceiver] = useState('');
    if (!sender.length) {
        axios.get(`http://localhost:8000/api/users/${props.sender}`).then(res => {
            setSender(res.data);
        });
    }
    if (!receiver.length) {
        axios.get(`http://localhost:8000/api/users/${props.receiver}`).then(res => {
            setReceiver(res.data);
        });
    }
//{users ? users.map(user => <UserData user={user}/>) : "Пользователей нет"}
    return (
        <React.Fragment>
            <Table>
                <tbody>
                    <tr> Отправитель: {sender} </tr>
                    <tr> Получатель: {receiver} </tr>
                </tbody>
            </Table>
        </React.Fragment>
    )
}


export default Users;
